span.onclick:
navigator.clipboard.writeText(document.getElementById('msg_1').innerText);
                       this.style.color='#2ECC71';
                       this.classList.remove('fa-clone'); 
                       this.classList.add('fa-check'); 
                       setTimeout(()=>{ 
                           this.classList.remove('fa-check'); 
                           this.classList.add('fa-clone'); 
                           this.style.color='#4A4A4A'; 
                       },1200);

